import { useRouteError, userRouterError } from "react-router-dom";

import PageContent from "../components/PageContent";

function ErrorPage() {
  const error = useRouteError();

  let title = "An error occured!";
  let message = "Someting went wrong!";

  if (error.status === 500) {
    message = error.data.message;
  }
  if(error.status === 404){
    title = 'Not found!'
    message = 'Could not fund resource or page'
  }

  return (
    <PageContent title="An error occured!">
      <p>Someting went wrong!</p>
    </PageContent>
  );
}

export default ErrorPage;
