import { createSlice } from "@reduxjs/toolkit";

const CounterSlince = createSlice({
  name: "cart",
  initialState: { item: [], totalQuantity: 0 },
  reducers: {
    increment(state) {
      state.totalQuantity = state.totalQuantity + 1;
    },
    decrement(state) {
      state.totalQuantity = state.totalQuantity - 1;
    },
  },
});
export const counterActions = CounterSlince.actions;
export default CounterSlince;
