import { createSlice } from "@reduxjs/toolkit";

const CounterSlince = createSlice({
  name: "cart",
  initialState: { items: [ ], totalQuantity: 0 },
  reducers: {

    addItemToCart(state,action) {
        const newItem = action.payload;
        const existingItem = state.items.find(item=>item.id == newItem.id);
        if(!existingItem){
            state.items.push({itemId:newItem.id , price:newItem.price,qauntity:1,totalPrice : newItem.price * newItem.qauntity});
        }

      state.totalQuantity = state.totalQuantity + 1;
    },
    decrement(state) {
      state.totalQuantity = state.totalQuantity - 1;
    },
  },
});
export const counterActions = CounterSlince.actions;
export default CounterSlince;
